import React, { useState } from 'react';

import { getOwnBalance, requestMint } from './Web3Client';
import { mintToken } from './Web3Client';

function App() {
	const [minted, setMinted] = useState(false);
	const [requested, setRequsted] = useState(false);
	const [balance, setBalance] = useState(0);

	const mint = () => {
		mintToken()
			.then((tx) => {
				console.log(tx);
				setMinted(true);
			})
			.catch((err) => {
				console.log(err);
			});
	};

	const requestM = () => {
		requestMint()
			.then((tx) => {
				console.log(tx);
				setRequsted(true);
			})
			.catch((err) => {
			console.log(err);
			});
	}

	const fetchBalance = () => {
		getOwnBalance()
			.then((balance) => {
				setBalance(balance);
			})
			.catch((err) => {
				console.log(err);
			});
	};

	return (
		<div className="App">
			{!minted ? (
				<button onClick={() => mint()}>Mint token</button>
			) : (
				<p>Token minted successfully!</p>
			)}
			<p>Your balance is {balance}</p>
			<button onClick={() => fetchBalance()}>Refresh balance</button>
			<p> </p>
			<button onClick={() => requestM()}>Request</button>
			<div className="my-3">
                  <input
                    type="text"
                    name="addr"
                    className="input input-bordered block w-full focus:ring focus:outline-none"
                    placeholder="ERC20 contract address"
                  />
                </div>
		</div>
	);
}

export default App;
