import React, { useState } from 'react';

import { getOwnBalance, requestMint, requestMint2 } from './Web3Client';
import { mintToken } from './Web3Client';

function App() {
	const [minted, setMinted] = useState(false);
	const [requested, setRequsted] = useState(false);
	const [balance, setBalance] = useState(0);

	const mint = () => {
		mintToken()
			.then((tx) => {
				console.log(tx);
				setMinted(true);
			})
			.catch((err) => {
				console.log(err);
			});
	};

	const requestM = () => {
		requestMint()
			.then((tx) => {
				console.log(tx);
				setRequsted(true);
			})
			.catch((err) => {
			console.log(err);
			});
	}

	const handleRequestMint = async (e) => {
    e.preventDefault();
    const data = new FormData(e.target);
		await requestMint2(data.get("gameID"), data.get("amount"));
	}

	const fetchBalance = () => {
		getOwnBalance()
			.then((balance) => {
				setBalance(balance);
			})
			.catch((err) => {
				console.log(err);
			});
	};

	return (
		<div className="App">

			<div className="m-4 credit-card w-full lg:w-3/4 sm:w-auto shadow-lg mx-auto rounded-xl bg-white">
          <div className="mt-4 p-4">
            <h1 className="text-xl font-semibold text-gray-700 text-center">
              RequestMint
            </h1>

            <form onSubmit={handleRequestMint}>
              <div className="my-3">
                <input
                  type="text"
                  name="gameID"
                  className="input input-bordered block w-full focus:ring focus:outline-none"
                  placeholder="gameID"
                />
              </div>
              <div className="my-3">
                <input
                  type="text"
                  name="amount"
                  className="input input-bordered block w-full focus:ring focus:outline-none"
                  placeholder="Amount to Mint"
                />
              </div>
              <footer className="p-4">
                <button
                  type="submit"
                  className="btn btn-primary submit-button focus:ring focus:outline-none w-full"
                >
                  submit
                </button>
              </footer>
            </form>
          </div>
        </div>

		</div>
	);
}

export default App;
