import React, { useState } from 'react';

import { getOwnBalance, requestMint, requestMint2, requestBurn, read } from './Web3Client';


function App() {
	const [requested, setRequsted] = useState(false);
	const [balance, setBalance] = useState(0);
	const [bookmark0, setBookmark0] = useState(0);
	const [bookmark1, setBookmark1] = useState([]);
	const [bookmark2, setBookmark2] = useState([]);
	const [bookmark3, setBookmark3] = useState([]);
	const readData=[];

	const handleRequestMint = async (e) => {
    e.preventDefault();
    const data = new FormData(e.target);
		await requestMint2(data.get("gameID"), data.get("amount"));
	}

	const handleRequestBurn = async (e) => {
    e.preventDefault();
    const data = new FormData(e.target);
		await requestBurn(data.get("gameID"), data.get("amount"));
	}

	const handleRead = async () => {
    readData = read;

	}

	const fetchBalance = () => {
		getOwnBalance()
			.then((balance) => {
				setBalance(balance);
			})
			.catch((err) => {
				console.log(err);
			});
	};

	return (
		<div className="App">

			<div>
          <div >
            <h1>
              RequestMint
            </h1>

            <form onSubmit={handleRequestMint}>
              <div>
                <input
                  type="text"
                  name="gameID"
                  placeholder="gameID"
                />
              </div>
              <div>
                <input
                  type="text"
                  name="amount"
                  placeholder="Amount to Mint"
                />
              </div>
              <footer>
                <button
                  type="submit"
                >
                  submit
                </button>
              </footer>
            </form>
          </div>
        </div>

				<div>
          <div >
            <h1>
              RequestBurn
            </h1>

            <form onSubmit={handleRequestBurn}>
              <div>
                <input
                  type="text"
                  name="gameID"
                  placeholder="gameID"
                />
              </div>
              <div>
                <input
                  type="text"
                  name="amount"
                  placeholder="Amount to Burn"
                />
              </div>
              <footer>
                <button
                  type="submit"
                >
                  submit
                </button>
              </footer>
            </form>
          </div>
        </div>

				<div>
          <div >
            <h1>
              Read
            </h1>

            <form onSubmit={handleRead}>

              <footer>
                <button
                  type="submit"
                >
                  read
                </button>
              </footer>

            </form>



          </div>
        </div>



		</div>
	);
}

export default App;
