	return (
		<div className="App">
			{!minted ? (
				<button onClick={() => mint()}>Mint token</button>
			) : (
				<p>Token minted successfully!</p>
			)}
			<p>Your balance is {balance}</p>
			<button onClick={() => fetchBalance()}>Refresh balance</button>
		</div>
	);
}

export default App;
