import NFTContractBuild from 'contracts/NFT.json';
import Web3 from 'web3';
import RequestContractBuild from 'contracts/RequestManager.json';
import factoryBuild from 'contracts/Factory.json';
import tokenBuild from 'contracts/ERC20Example.json';

let selectedAccount;
let erc20Contract;
let requestContract;
let BurnContract;
let readContract;
let erc20ExampleContract;
let factoryContract;
let isInitialized = false;

const Mmanager ="0x6d79791da87F7d014ae61afeFF833A3D06B89820";
const Tmanager ="0x2451F945BA4cAf4570D0958d815a6Af3a2E1a9c4";
const Emanager ="0xd03f4C997cF4Bd0c083b81D3BF8B48088FD278f9";
const Wmanager ="0xcd79904633516513A541D5Ce2c3984907AAE81eD";
const Pmanager ="0xc245D446Dff9407322638F444f23769991603939";

export const init = async () => {
	let provider = window.ethereum;

	if (typeof provider !== 'undefined') {
		provider
			.request({ method: 'eth_requestAccounts' })
			.then((accounts) => {
				selectedAccount = accounts[0];
				console.log(`Selected account is ${selectedAccount}`);
			})
			.catch((err) => {
				console.log(err);
				return;
			});

		window.ethereum.on('accountsChanged', function (accounts) {
			selectedAccount = accounts[0];
			console.log(`Selected account changed to ${selectedAccount}`);
		});
	}

	const web3 = new Web3(provider);

	const networkId = await web3.eth.net.getId();

	const erc20Abi = [
		{
			constant: true,
			inputs: [
				{
					name: '_owner',
					type: 'address'
				}
			],
			name: 'balanceOf',
			outputs: [
				{
					name: 'balance',
					type: 'uint256'
				}
			],
			payable: false,
			stateMutability: 'view',
			type: 'function'
		}
	];

	const requestabi = [{
		inputs: [
			{
				"internalType": "string",
				"name": "_gameID",
				"type": "string"
			},
			{
				"internalType": "uint256",
				"name": "_amount",
				"type": "uint256"
			}
		],
		name: "requestMint",
		outputs: [],
		stateMutability: "nonpayable",
		type : "function"
	}];

	const requestBurnabi = [{
		inputs: [
			{
				"internalType": "string",
				"name": "_gameID",
				"type": "string"
			},
			{
				"internalType": "uint256",
				"name": "_amount",
				"type": "uint256"
			}
		],
		name: "requestBurn",
		outputs: [],
		stateMutability: "nonpayable",
		type: "function"
	}];

	const readabi = [
		{
			inputs: [],
			name: "read",
			outputs: [
				{
					"internalType": "uint256",
					"name": "",
					"type": "uint256"
				},
				{
					"internalType": "string[]",
					"name": "",
					"type": "string[]"
				},
				{
					"internalType": "uint256[]",
					"name": "",
					"type": "uint256[]"
				},
				{
					"internalType": "bool[]",
					"name": "",
					"type": "bool[]"
				}
			],
			stateMutability: "view",
			type: "function"
		}
	];

	const erc20ExAbi = [
		{
			inputs: [],
			name: "name",
			"outputs": [
				{
					"internalType": "string",
					"name": "",
					"type": "string"
				}
			],
			stateMutability: "view",
			type: "function"
		},
		{
			inputs: [],
			name: "symbol",
			outputs: [
				{
					"internalType": "string",
					"name": "",
					"type": "string"
				}
			],
			stateMutability: "view",
			type: "function"
		},
		{
			inputs: [],
			name: "totalSupply",
			outputs: [
				{
					"internalType": "uint256",
					"name": "",
					"type": "uint256"
				}
			],
			stateMutability: "view",
			type: "function"
		}

	];

	factoryContract = new web3.eth.Contract(
		factoryBuild.abi, "0xb774abA64eE5e6DE4238D48AFC41D3EeB2A2D775"
	);

	erc20Contract = new web3.eth.Contract(
		erc20Abi,
		// Dai contract on Rinkeby
		'0x0fa0B2a9fb28752E37b36Ce5Ccc30C15B84980A6'
	);

	BurnContract = new web3.eth.Contract(
		requestBurnabi, '0x0fa0B2a9fb28752E37b36Ce5Ccc30C15B84980A6'
	);

	readContract = new web3.eth.Contract(
		readabi, '0x0fa0B2a9fb28752E37b36Ce5Ccc30C15B84980A6'
	);
	requestContract = new web3.eth.Contract(
		requestabi, '0x0fa0B2a9fb28752E37b36Ce5Ccc30C15B84980A6'
	);

	erc20ExampleContract = new web3.eth.Contract(
		erc20ExAbi,
		'0xB93c86916ca6c5526faF2F2Fb83c8990283c3435'
	);


	isInitialized = true;
};

export const getOwnBalance = async () => {
	if (!isInitialized) {
		await init();
	}

	return erc20Contract.methods
		.balanceOf(selectedAccount)
		.call()
		.then((balance) => {
			return Web3.utils.fromWei(balance);
		});
};

export const requestMint = async () => {
	if (!isInitialized) {
		await init();
	}

	return requestContract.methods
		.requestMint("bbb", 10)
		.send({from: selectedAccount});
}

export const requestMint2 = async (gameID, amount, managerAddr) => {
	if (!isInitialized) {
		await init();
	}

	let mintContract;
	let provider = window.ethereum;
	const web3 = new Web3(provider);
	mintContract = new web3.eth.Contract(
		RequestContractBuild.abi, managerAddr
	);

	return requestContract.methods
		.requestMint(gameID, amount)
		.send({from: selectedAccount});
}

export const requestBurn = async (gameID, amount) => {
	if (!isInitialized) {
		await init();
	}

	return BurnContract.methods
		.requestBurn(gameID, amount)
		.send({from: selectedAccount});
}

export const read = async() => {
	if (!isInitialized) {
		await init();
	}

	return readContract.methods.read().send({from: selectedAccount});
}

export const getTokenInfo = async (address) => {
	if (!isInitialized) {
		await init();
	}
	let tokenContract;
	let provider = window.ethereum;
	const web3 = new Web3(provider);
	tokenContract = new web3.eth.Contract(
		tokenBuild.abi, address
	);
	const name = await tokenContract.methods.name().call();
	return name;
};

export const getTokenSymbol = async (address) => {
	if (!isInitialized) {
		await init();
	}
	let tokenContract;
	let provider = window.ethereum;
	const web3 = new Web3(provider);
	tokenContract = new web3.eth.Contract(
		tokenBuild.abi, address
	);
	const symbol = await tokenContract.methods.symbol().call();
	return symbol;
};

export const getTokenSupply = async (address) => {
	if (!isInitialized) {
		await init();
	}
	let tokenContract;
	let provider = window.ethereum;
	const web3 = new Web3(provider);
	tokenContract = new web3.eth.Contract(
		tokenBuild.abi, address
	);
	const supply = await tokenContract.methods.totalSupply().call();
	return supply;
};
